using cAlgo.API.Indicators;
using System;
using System.IO;
using System.Text;
using System.Globalization;
using System.Collections.Generic;

namespace cAlgo.Robots
{
    [Robot(TimeZone = TimeZones.UTC, AccessRights = AccessRights.FullAccess)]
    public class ExportOHLCWithIndicators : Robot
    {
        // Indicator declarations
        private MovingAverage _sma20;
        private RelativeStrengthIndex _rsi;
        private AverageTrueRange _atr;
        private MacdHistogram _macd;
        private BollingerBands _bollingerBands;
        private CommodityChannelIndex _cci;
        private KeltnerChannels _keltnerChannels;
        private ExponentialMovingAverage _ema50;
        private ExponentialMovingAverage _ema200;
        private StochasticOscillator _stochastic;
        private WilliamsPctR _williamsR;
        private AwesomeOscillator _ao;
        private AcceleratorOscillator _accelerationOscillator;
        private Fractals _fractals;
        private ParabolicSAR _parabolicSar;
        private DirectionalMovementSystem _adx;
        private LinearRegressionForecast _regressionForecast;
        private LinearRegressionIntercept _regressionIntercept;

        // Custom volume ROC implementation
        private double[] _volumeROC;
        private int _volumeROCPeriod = 14;

        private string _filePath;
        private bool _headerWritten = false;

        // User parameters for saving options
        [Parameter("Save OHLC", DefaultValue = true)]
        public bool SaveOHLC { get; set; }

        [Parameter("Save SMA 20", DefaultValue = true)]
        public bool SaveSMA20 { get; set; }

        [Parameter("Save RSI", DefaultValue = true)]
        public bool SaveRSI { get; set; }

        [Parameter("Save ATR", DefaultValue = true)]
        public bool SaveATR { get; set; }

        [Parameter("Save MACD", DefaultValue = true)]
        public bool SaveMACD { get; set; }

        [Parameter("Save Bollinger Bands", DefaultValue = true)]
        public bool SaveBollingerBands { get; set; }

        [Parameter("Save CCI", DefaultValue = true)]
        public bool SaveCCI { get; set; }

        [Parameter("Save Keltner Channels", DefaultValue = true)]
        public bool SaveKeltnerChannels { get; set; }

        [Parameter("Save EMA50", DefaultValue = true)]
        public bool SaveEMA50 { get; set; }

        [Parameter("Save EMA200", DefaultValue = true)]
        public bool SaveEMA200 { get; set; }

        [Parameter("Save Stochastic", DefaultValue = true)]
        public bool SaveStochastic { get; set; }

        [Parameter("Save Williams %R", DefaultValue = true)]
        public bool SaveWilliamsR { get; set; }

        [Parameter("Save Awesome Oscillator", DefaultValue = true)]
        public bool SaveAO { get; set; }

        [Parameter("Save Acceleration Oscillator", DefaultValue = true)]
        public bool SaveAcceleration { get; set; }

        [Parameter("Save Fractals", DefaultValue = true)]
        public bool SaveFractals { get; set; }

        [Parameter("Save Parabolic SAR", DefaultValue = true)]
        public bool SaveParabolicSAR { get; set; }

        [Parameter("Save ADX", DefaultValue = true)]
        public bool SaveADX { get; set; }

        [Parameter("Save Regression", DefaultValue = true)]
        public bool SaveRegression { get; set; }

        [Parameter("Save Volume ROC", DefaultValue = true)]
        public bool SaveVolumeROC { get; set; }

        [Parameter("Volume ROC Period", DefaultValue = 14)]
        public int VolumeROCPeriod { get; set; }

        protected override void OnStart()
        {
            try
            {
                // Initialize indicators
                _sma20 = Indicators.MovingAverage(Bars.ClosePrices, 20, MovingAverageType.Simple);
                _rsi = Indicators.RelativeStrengthIndex(Bars.ClosePrices, 14);
                _atr = Indicators.AverageTrueRange(14, MovingAverageType.Simple);
                _macd = Indicators.MacdHistogram(Bars.ClosePrices, 12, 26, 9);
                _bollingerBands = Indicators.BollingerBands(Bars.ClosePrices, 20, 2, MovingAverageType.Simple);
                _cci = Indicators.CommodityChannelIndex(20);
                _keltnerChannels = Indicators.KeltnerChannels(20, MovingAverageType.Exponential, 10, MovingAverageType.Simple, 2);
                _ema50 = Indicators.ExponentialMovingAverage(Bars.ClosePrices, 50);
                _ema200 = Indicators.ExponentialMovingAverage(Bars.ClosePrices, 200);
                _stochastic = Indicators.StochasticOscillator(14, 3, 3, MovingAverageType.Simple);
                _williamsR = Indicators.WilliamsPctR(14);
                _ao = Indicators.AwesomeOscillator();
                _accelerationOscillator = Indicators.AcceleratorOscillator();
                _fractals = Indicators.Fractals(5);
                _parabolicSar = Indicators.ParabolicSAR(0.02, 0.2);
                _adx = Indicators.DirectionalMovementSystem(14);
                _regressionForecast = Indicators.LinearRegressionForecast(Bars.ClosePrices, 14);
                _regressionIntercept = Indicators.LinearRegressionIntercept(Bars.ClosePrices, 14);

                // Set Volume ROC period from parameter
                _volumeROCPeriod = VolumeROCPeriod;

                // Initialize Volume ROC array with the current bars count
                _volumeROC = new double[Bars.Count];

                // Calculate initial Volume ROC values
                CalculateVolumeROC();

                // Set the file path - ensure directory exists
                string documentsFolder = Environment.GetFolderPath(Environment.SpecialFolder.MyDocuments);
                _filePath = Path.Combine(documentsFolder, "OHLC_with_Indicators.csv");

                // Create the directory if it doesn't exist
                Directory.CreateDirectory(Path.GetDirectoryName(_filePath));

                // Write the header (we'll write it first time in OnBar)
                _headerWritten = false;

                Print("Export will be saved to: " + _filePath);
            }
            catch (Exception ex)
            {
                Print($"Error in OnStart: {ex.Message}");
                Print($"Stack trace: {ex.StackTrace}");
            }
        }

        private void CalculateVolumeROC()
        {
            try
            {
                // Calculate Volume Rate of Change for all bars
                for (int i = 0; i < Bars.Count; i++)
                {
                    if (i >= _volumeROCPeriod)
                    {
                        double currentVolume = Bars.TickVolumes[i];
                        double pastVolume = Bars.TickVolumes[i - _volumeROCPeriod];

                        if (pastVolume != 0)
                        {
                            _volumeROC[i] = ((currentVolume - pastVolume) / pastVolume) * 100;
                        }
                        else
                        {
                            // Handle division by zero
                            _volumeROC[i] = 0;
                        }
                    }
                    else
                    {
                        _volumeROC[i] = 0;
                    }
                }
            }
            catch (Exception ex)
            {
                Print($"Error in CalculateVolumeROC: {ex.Message}");
            }
        }

        private void WriteHeader()
        {
            try
            {
                // Build header columns
                var headerColumns = new List<string>();
                headerColumns.Add("Timestamp");
                if (SaveOHLC)
                {
                    headerColumns.Add("Open");
                    headerColumns.Add("High");
                    headerColumns.Add("Low");
                    headerColumns.Add("Close");
