                if (SaveRSI)
                    headerColumns.Add("RSI");
                if (SaveATR)
                    headerColumns.Add("ATR");
                if (SaveMACD)
                    headerColumns.Add("MACD");
                if (SaveBollingerBands)
                {
                    headerColumns.Add("Bollinger_Upper");
                    headerColumns.Add("Bollinger_Lower");
                }
                if (SaveCCI)
                    headerColumns.Add("CCI");
                if (SaveKeltnerChannels)
                {
                    headerColumns.Add("KeltnerChannel_Top");
                    headerColumns.Add("KeltnerChannel_Bottom");
                }
                if (SaveEMA50)
                    headerColumns.Add("EMA50");
                if (SaveEMA200)
                    headerColumns.Add("EMA200");
                if (SaveStochastic)
                {
                    headerColumns.Add("StochK");
                    headerColumns.Add("StochD");
                }
                if (SaveWilliamsR)
                    headerColumns.Add("WilliamsR");
                if (SaveAO)
                    headerColumns.Add("AO");
                if (SaveAcceleration)
                    headerColumns.Add("Acceleration");
                if (SaveFractals)
                {
                    headerColumns.Add("Fractal_Up");
                    headerColumns.Add("Fractal_Down");
                }
                if (SaveParabolicSAR)
                    headerColumns.Add("ParabolicSAR");
                if (SaveADX)
                {
                    headerColumns.Add("ADX");
                    headerColumns.Add("DI_Plus");
                    headerColumns.Add("DI_Minus");
                }
                if (SaveRegression)
                {
                    headerColumns.Add("RegressionForecast");
                    headerColumns.Add("RegressionIntercept");
                }
                if (SaveVolumeROC)
                    headerColumns.Add("VolumeROC");

                // Create a new file or overwrite existing
                using (var writer = new StreamWriter(_filePath, false, Encoding.UTF8))
                {
                    writer.WriteLine(string.Join(",", headerColumns));
                }

                _headerWritten = true;
                Print("Header written successfully");
            }
            catch (Exception ex)
            {
                Print($"Error writing header: {ex.Message}");
                Print($"Stack trace: {ex.StackTrace}");
            }
        }

        protected override void OnBar()
        {
            try
            {
                // Write header if not written yet
                if (!_headerWritten)
                {
                    WriteHeader();
                }

                var index = Bars.Count - 1;

                // Ensure array size matches current bar count
                if (_volumeROC.Length <= index)
                {
                    Array.Resize(ref _volumeROC, index + 100); // Resize with some extra capacity
                }

                // Update Volume ROC for current bar
                if (index >= _volumeROCPeriod)
                {
                    double currentVolume = Bars.TickVolumes[index];
                    double pastVolume = Bars.TickVolumes[index - _volumeROCPeriod];

                    if (pastVolume != 0)
                    {
                        _volumeROC[index] = ((currentVolume - pastVolume) / pastVolume) * 100;
                    }
                    else
                    {
                        _volumeROC[index] = 0;
                    }
